update.foreach:
update formdata
set Control_VALUE_=#{item.Control_VALUE_}
where PROC_INST_ID_=#{item.PROC_INST_ID_} and Control_ID_=#{item.Control_ID_}

mapper.select:
-- 获取总任务数
select count(*)
from user;

-- 获取历史任务的表单信息
SELECT *
FROM `formdata`
WHERE PROC_INST_ID_ = #{processInstanceId}
  and FORM_KEY_ = #{formKey}
